function initSootEasterEgg(){document.querySelectorAll(".soot-sprite").forEach((t=>{t.addEventListener("click",(o=>{o.stopPropagation(),triggerSootExplosion(t,o)}))}))}function triggerSootExplosion(t,o){if(t.classList.contains("soot-exploding"))return;const e=o.clientX,s=o.clientY;t.classList.add("soot-exploding"),t.style.animation="sootDisintegrate 1100ms ease-out forwards";const n=22+Math.floor(16*Math.random());for(let t=0;t<n;t++)createParticle(e,s);setTimeout((()=>{t.classList.add("soot-hidden"),t.classList.remove("soot-exploding"),t.style.animation=""}),1100)}function createParticle(t,o){const e=document.createElement("div");e.className="soot-particle";const s=4+4*Math.random();e.style.width=s+"px",e.style.height=s+"px";const n=Math.random()*Math.PI*2,a=40+120*Math.random(),i=-20-60*Math.random(),r=Math.round(Math.cos(n)*a)+"px",d=Math.round(Math.sin(n)*a+i)+"px",l=Math.round(720*(Math.random()-.5))+"deg";e.style.left=t-s/2+"px",e.style.top=o-s/2+"px",e.style.setProperty("--tx",r),e.style.setProperty("--ty",d),e.style.setProperty("--r",l),document.body.appendChild(e),setTimeout((()=>{e.remove()}),1450)}document.addEventListener("DOMContentLoaded",(()=>{setTimeout(initSootEasterEgg,200)}));